import React, {} from "react";

// Navigation components

import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import {Text, Dimensions} from 'react-native'
import FontAwesome from "react-native-vector-icons/FontAwesome";

//Import screens

import Home from "../screens/Home";
import About from "../screens/About";
import Detail from "../screens/Detail";
import Splash from "../screens/Splash";
import Login from "../screens/Login";

const fullScreenWidth = Dimensions.get('window').width;

const Stack = createStackNavigator();

function SplashandLoginStackScreen() {
    return (
        <Stack.Navigator>
            <Stack.Screen name='Splash' component={Splash}></Stack.Screen>
            <Stack.Screen name='Login' component={Login}></Stack.Screen>
        </Stack.Navigator>
    )
}

function HomeStackScreen() {
    return (
        <Stack.Navigator>
            <Stack.Screen name='Home' component={Home}></Stack.Screen>
        </Stack.Navigator>
    )
}

function DetailStackScreen() {
    return (
        <Stack.Navigator>
            <Stack.Screen name='Detail' component={Detail}></Stack.Screen>
        </Stack.Navigator>
    )
}

function AboutStackScreen() {
    return (
        <Stack.Navigator>
            <Stack.Screen name='About' component={About}></Stack.Screen>
        </Stack.Navigator>
    )
}

const Tab = createBottomTabNavigator();

export default function Navigation(props) {
    return (
        <NavigationContainer>

            <SplashandLoginStackScreen/>





            <Tab.Navigator
                screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, size, color }) => {
            let iconName;
            if (route.name === "Home"){
            //   filePath = require("../assets/Lottie/82488-team-members-meetup.json");
                iconName = 'home';
                size = focused ? 100 : 45;
                color = focused ? "blue" : "#555"
                
            } else if (route.name === "Detail") {
            //   filePath = require("../assets/Lottie/57946-profile-user-card.json");
                iconName = 'home';  
                size = focused ? 100 : 45;
                color = focused ? "blue" : "#555"
            }
            else if (route.name === "About") {
            //   filePath = require("../assets/Lottie/75746-about-us.json");
                iconName = 'home';      
                size = focused ? 100 : 45;
                color = focused ? "blue" : "#555"
            
            }
            
            return(
                // <LottieView source={filePath} autoPlay={focused} loop />
                <FontAwesome name={iconName} size={size} color={color}/>
            )
          },
          
          tabBarLabelStyle: {
            fontSize: 11,
            fontWeight: 'bold'
          },  

          headerShown: true,
          headerTitleAlign: 'center',
          headerStyle: {
            backgroundColor: "orange"
          },
          headerTintColor: "#ffffff",
          headerTitleStyle: {
            fontWeight: 'bold'
          },
    
          
        })}
        >
            
            <Tab.Screen name="Home" component={HomeStackScreen}/>
            <Tab.Screen name="Detail" component={DetailStackScreen}/>
            <Tab.Screen name="About" component={AboutStackScreen}/>
        </Tab.Navigator>
        </NavigationContainer>
    )
}
