import React from "react";
import { View, StyleSheet, Text, Dimensions, TextInput, TouchableOpacity, TouchableWithoutFeedback, Button, AppRegistry} from "react-native";
import LottieView from "lottie-react-native"
import Splash from "./Splash";
import FontAweSome5 from "react-native-vector-icons/FontAwesome5"

const {height, width} = Dimensions.get('window')
const sourceWidth = 3200 // the width of the animation
const sourceHeight = 3200 // the height of the animation

export default class Login extends React.Component {
    
    constructor(props){
        super(props)
        this.state={
            press: false,
            securePass: true,
        }
        this.showPassHandler = this.showPassHandler.bind(this)
      }

    showPassHandler(){
        this.setState({
            press: !this.state.press,
            securePass: !this.state.securePass
        })
    }

    render ()
    {
        return(
       
        
        <View
            contenVContainerStyle={{ width: width, height: width * sourceHeight / sourceWidth}} style={styles.body}>
            <LottieView source={require("../../assets/Lottie/50124-user-profile.json")}
            // style={{width: width, height: width * sourceHeight / sourceWidth, marginTop: 28}}
            style={{width: 200, height: 200, marginTop: 28}}
            autoPlay={true} loop
            />
            <LottieView source={require("../../assets/Lottie/10152-login-animation.json")}
            style={{width: 50, height: 50, marginTop: 5}}
            autoPlay={true} loop
             />

            <View>
                <TextInput 
                    style={styles.input}
                    placeholder={'Username'}
                    color={'black'}
                ></TextInput>
                <FontAweSome5 name={"user-circle"} size={25} style={styles.inputIcon} solid={true}/>
            </View>

            <View style={{marginTop: 10}}>    

                <TextInput 
                        style={styles.input}
                        placeholder={'Password'}
                        secureTextEntry={this.state.securePass}
                        color={'black'}
                ></TextInput>

                <FontAweSome5 name={"key"} size={23} style={styles.inputIcon}/>
                
                <TouchableOpacity 
                    style={styles.btnEye}
                    onPress={this.showPassHandler}
                >
                    <FontAweSome5 name={this.state.press ? "eye" : "eye-slash"} size={20} solid={true}/>
                </TouchableOpacity>   
            </View>

            {/* <View 
                style={{
                    height: 100,
                    justifyContent: 'center',
                    alignItems: 'center',
                }}    
            > 
                <Button
                    style={styles.btnLogin}
                >
                    <Text>Login</Text>
                </Button>
            </View> */}
            
        </View>
       
       
        )
    }
}

const styles = StyleSheet.create({
    body: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: 'orange'
    },

    text: {
        fontSize: 30,
        color: "black"
    },

    input: {
        width: width - 45,
        height: 40,
        borderRadius: 25,
        fontSize: 16,
        paddingLeft: 45,
        backgroundColor: "white",
        color: "rgba(255, 255, 255, 0.7)",
        marginHorizontal: 25,
       position: "relative"
    },

    inputIcon: {
        position: "absolute",
        top: 8,
        left: 37
    },

    container: {
        flex: 1
      },

    btnEye: {
        position: "absolute",
        top: 8,
        right: 37
    },

    btnLogin: {
        alignSelf: "center",
        backgroundColor: "#4632A1",
        width: width / 2,
        justifyContent: "center"
    }
})
